  ('q-c4t-w1', 'prod-cert4-trade', 'ver-cert4-trade-v1', 'Prepare a comprehensive method statement for high-risk construction work including plant, personnel, safety controls, and sequences.', 'short_answer', null, 'Writing', 5, 'Method: Excavation using 20T excavator. Personnel: 1 operator, 1 spotter, 1 supervisor. Controls: Exclusion zones, service location, shoring system, daily inspections. Sequence: Locate services, establish barriers, excavate in 1m lifts, install shoring progressively.', 1.0, 'Prepare a comprehensive method statement for high-risk construction work including plant, personnel, safety controls, and sequences.', 'short_answer', 'Writing', 'Method: Excavation using 20T excavator. Personnel: 1 operator, 1 spotter, 1 supervisor. Controls: Exclusion zones, service location, shoring system, daily inspections. Sequence: Locate services, establish barriers, excavate in 1m lifts, install shoring progressively.', 1.0, 'Develops technical documentation', 4),
  ('q-c4t-w2', 'prod-cert4-trade', 'ver-cert4-trade-v1', 'Write a formal project progress report addressing schedule, budget variance, quality issues, and risk mitigation.', 'short_answer', null, 'Writing', 6, 'Project 85% complete, 2 weeks behind schedule due to weather delays. Budget variance +3% due to material price increases. Quality audit passed. Mitigation: Additional crew allocated, value engineering options being explored.', 1.0, 'Write a formal project progress report addressing schedule, budget variance, quality issues, and risk mitigation.', 'short_answer', 'Writing', 'Project 85% complete, 2 weeks behind schedule due to weather delays. Budget variance +3% due to material price increases. Quality audit passed. Mitigation: Additional crew allocated, value engineering options being explored.', 1.0, 'Writes formal business reports', 4),
  ('q-c4t-w3', 'prod-cert4-trade', 'ver-cert4-trade-v1', 'Draft a non-conformance report that analyses root cause and proposes corrective and preventive actions.', 'short_answer', null, 'Writing', 7, 'Non-conformance: Concrete strength test failed (25MPa vs 32MPa specified). Root cause: Incorrect water/cement ratio. Corrective action: Remove and replace affected concrete. Preventive action: Implement batch testing protocol and supplier audits.', 1.0, 'Draft a non-conformance report that analyses root cause and proposes corrective and preventive actions.', 'short_answer', 'Writing', 'Non-conformance: Concrete strength test failed (25MPa vs 32MPa specified). Root cause: Incorrect water/cement ratio. Corrective action: Remove and replace affected concrete. Preventive action: Implement batch testing protocol and supplier audits.', 1.0, 'Writes analytical reports', 4),
  -- Oral Communication
  ('q-c4t-o1', 'prod-cert4-trade', 'ver-cert4-trade-v1', 'You are leading a site safety induction for a new subcontractor team. What elements must you cover?', 'short_answer', null, 'Oral Communication', 8, 'Site hazards, emergency procedures, PPE requirements, permit systems, reporting procedures, environmental controls, and specific work method statements', 1.0, 'You are leading a site safety induction for a new subcontractor team. What elements must you cover?', 'short_answer', 'Oral Communication', 'Site hazards, emergency procedures, PPE requirements, permit systems, reporting procedures, environmental controls, and specific work method statements', 1.0, 'Delivers formal presentations', 4),
  ('q-c4t-o2', 'prod-cert4-trade', 'ver-cert4-trade-v1', 'During a client meeting, how do you handle a dispute about scope of work?', 'multiple_choice', '["Argue your position forcefully", "Refer to contract documents, discuss professionally, seek mutually acceptable solution", "Walk out of the meeting", "Agree to everything the client says"]', 'Oral Communication', 9, 'Refer to contract documents, discuss professionally, seek mutually acceptable solution', 1.0, 'During a client meeting, how do you handle a dispute about scope of work?', 'multiple_choice', 'Oral Communication', 'Refer to contract documents, discuss professionally, seek mutually acceptable solution', 1.0, 'Negotiates and resolves issues', 4),
  -- Numeracy
  ('q-c4t-n1', 'prod-cert4-trade', 'ver-cert4-trade-v1', 'Calculate the labour cost for a project: 3 workers for 40 hours at $45/hr, 1 supervisor for 40 hours at $65/hr, plus 25% for overheads and profit.', 'short_answer', null, 'Numeracy', 10, '10125', 1.0, 'Calculate the labour cost for a project: 3 workers for 40 hours at $45/hr, 1 supervisor for 40 hours at $65/hr, plus 25% for overheads and profit.', 'short_answer', 'Numeracy', '10125', 1.0, 'Complex cost calculations', 4),
  ('q-c4t-n2', 'prod-cert4-trade', 'ver-cert4-trade-v1', 'A steel beam deflects 15mm under load. If the maximum allowable deflection is span/250 and the span is 6m, is this acceptable?', 'short_answer', null, 'Numeracy', 11, 'No, maximum allowable is 24mm (6000/250), actual deflection of 15mm is acceptable', 1.0, 'A steel beam deflects 15mm under load. If the maximum allowable deflection is span/250 and the span is 6m, is this acceptable?', 'short_answer', 'Numeracy', 'No, maximum allowable is 24mm (6000/250), actual deflection of 15mm is acceptable', 1.0, 'Engineering calculations', 4),
  ('q-c4t-n3', 'prod-cert4-trade', 'ver-cert4-trade-v1', 'Calculate the critical path duration: Task A (3 days), Task B (5 days, depends on A), Task C (4 days, depends on A), Task D (2 days, depends on B and C).', 'short_answer', null, 'Numeracy', 12, '10', 1.0, 'Calculate the critical path duration: Task A (3 days), Task B (5 days, depends on A), Task C (4 days, depends on A), Task D (2 days, depends on B and C).', 'short_answer', 'Numeracy', '10', 1.0, 'Project scheduling mathematics', 4),
  ('q-c4t-n4', 'prod-cert4-trade', 'ver-cert4-trade-v1', 'If material wastage is typically 8% and you need 250m² of finished area, how much material should you order?', 'short_answer', null, 'Numeracy', 13, '270', 1.0, 'If material wastage is typically 8% and you need 250m² of finished area, how much material should you order?', 'short_answer', 'Numeracy', '270', 1.0, 'Wastage calculations', 4),
  -- Digital Literacy
  ('q-c4t-d1', 'prod-cert4-trade', 'ver-cert4-trade-v1', 'Which software would you use to create a project schedule with dependencies, resources, and critical path analysis?', 'multiple_choice', '["Microsoft Word", "Microsoft Project or Primavera", "Calculator", "Email"]', 'Digital Literacy', 14, 'Microsoft Project or Primavera', 1.0, 'Which software would you use to create a project schedule with dependencies, resources, and critical path analysis?', 'multiple_choice', 'Digital Literacy', 'Microsoft Project or Primavera', 1.0, 'Uses specialized project software', 4),
  ('q-c4t-d2', 'prod-cert4-trade', 'ver-cert4-trade-v1', 'How would you implement version control for project documentation across multiple team members?', 'multiple_choice', '["Email files back and forth", "Use collaborative platform like SharePoint with version history", "Keep files on personal computer", "Print everything"]', 'Digital Literacy', 15, 'Use collaborative platform like SharePoint with version history', 1.0, 'How would you implement version control for project documentation across multiple team members?', 'multiple_choice', 'Digital Literacy', 'Use collaborative platform like SharePoint with version history', 1.0, 'Manages digital workflows', 4);

