  -- Reading
  ('q-c3c-r1', 'prod-cert3-community', 'ver-cert3-community-v1', 'Interpret this aged care standard: "Organisations must have systems to prevent and respond to abuse". What is required?', 'short_answer', null, 'Reading', 1, 'Systems for preventing abuse, responding to abuse reports, and protecting clients must be implemented', 1.0, 'Interpret this aged care standard: "Organisations must have systems to prevent and respond to abuse". What is required?', 'short_answer', 'Reading', 'Systems for preventing abuse, responding to abuse reports, and protecting clients must be implemented', 1.0, 'Interprets compliance requirements', 3),
  ('q-c3c-r2', 'prod-cert3-community', 'ver-cert3-community-v1', 'Review a client care plan and identify the specific support needs for someone with dementia.', 'short_answer', null, 'Reading', 2, 'Needs routine, memory aids, gentle redirection, familiar environment, and patience with repetition', 1.0, 'Review a client care plan and identify the specific support needs for someone with dementia.', 'short_answer', 'Reading', 'Needs routine, memory aids, gentle redirection, familiar environment, and patience with repetition', 1.0, 'Analyses care requirements', 3),
